mapper.sql:
SELECT * from ( SELECT t1.ID, t1.report_user_id, t1.task_id, t1.report_day, t1.duration, t1.remark, t1.audit_status, t1.create_user_id, t1.create_ts, t1.update_user_id, t1.update_ts, t2.task_name -- okrid ,omo.id okr_id -- 团队id ,ot2.id team_id -- 产品id ,(case when oac.id = '01' then null else opi.id end ) tem_product_id ,(case when oac.id = '00' then opi.id else oc.id end ) product_id ,oac.id type_id -- 客户id ,(case when oac.id = '00' then null else oc.id end) tem_cust_id FROM t_okr_daily t1 LEFT JOIN t_okr_task t2 ON t1.task_id = t2.id left join T_OKR_TASK_APPORTION otp on otp.task_id = t2.id LEFT join T_OKR_APPORTION_CATEGORY oac on otp.category_id = oac.id left join T_OKR_PRODUCT_INFO opi on opi.id = otp.apportion_name_id left join T_OKR_CUSTOMER oc on oc.id = otp.apportion_name_id LEFT JOIN T_OKR_TEAMS ot2 ON ot2.id = t2.belong_team LEFT JOIN T_OKR_TASK_KR_REL otkr ON otkr.task_id = t2.id LEFT JOIN T_OKR_MANAGE_OBJECTIVES omo ON omo.id = otkr.kr_id WHERE 1 = 1
AND to_char(t1.report_day,'yyyy-MM-dd') = #{condition.reportDayStr}
AND t1.report_user_id = #{condition.reportUserId}
AND t1.report_user_id in
AND t1.report_user_id = #{userId}
AND t1.task_id = #{condition.taskId}
AND to_char(t1.report_day,'yyyy-MM-dd') BETWEEN #{condition.reportStartDayStr} AND #{condition.reportEndDayStr}
AND ot2.id = #{condition.teamId}
AND omo.id = #{condition.okrId}
AND oac.id = #{condition.categoryId}
ORDER BY t1.report_day DESC ,t1.update_ts DESC)t
WHERE product_id = #{condition.productId}

mapper.select:
select COALESCE(sum(COALESCE(t1.duration,0)),0) from
t_okr_daily t1
where t1.task_id = #{taskId}
