  count(t1.*)
FROM t_okr_task t1
LEFT join t_okr_sys_user t2 on t1.create_user_id = t2.id
left join T_OKR_TEAMS t3 on t1.belong_team = t3.id


        and (t1.task_name LIKE CONCAT('%', #{vo.searchKey }, '%') or t1.task_remark LIKE CONCAT('%', #{vo.searchKey }, '%'))


        and t1.belong_team = #{vo.teamId}


        AND t1.create_ts >= #{vo.queryStartDate}


        AND t1.create_ts <= #{vo.queryEndDate}

SELECT DISTINCT t1.project_id FROM ( SELECT tta.project_id FROM T_OKR_DAILY td INNER JOIN T_OKR_TASK_APPORTION tta ON td.task_id = tta.task_id INNER JOIN t_okr_task TT ON TTA.task_id = TT.ID
and td.report_user_id = #{reportUserId}
and tt.confirm_user_id = #{confirmUserId}
ORDER BY td.report_day DESC ) t1
AND ar.create_ts <= #{vo.queryEndDate}

SELECT
    COUNT ( td.* ) relTaskNum,
    COALESCE ( SUM ( td.DURATION ), 0 ) totalDuration
FROM
    T_OKR_DAILY td
    INNER JOIN t_okr_task_apportion tta ON td.task_id = tta.task_id


       AND td.report_user_id = #{reportUserId}


       AND tta.project_id = #{projectId}


        AND td.report_day >= #{reportStartDate}


        AND td.report_day <= #{reportEndDate}

SELECT * from ( SELECT ot.id task_id ,ot.task_name ,otur.user_id ,(case when oac.id = '01' then null else opt.id end ) tem_product_id ,(case when oac.id = '01' then null else opt.product_name end ) tem_product_name ,(case when oac.id = '00' then null else oc.id end) tem_cust_id ,(case when oac.id = '00' then null else oc.name end ) tem_cust_name ,(case when oac.id = '00' then opt.id else oc.id end ) product_id ,(case when oac.id = '00' then opt.product_name else oc.name end ) product_name ,oac.id category_id ,oac.category_name ,ot2.name team_name ,ot2.id team_id ,omo.id okr_id ,omo.name okr_Name FROM T_OKR_TASK ot left join T_OKR_TASK_USER_REL otur on ot.id = otur.task_id LEFT join T_OKR_TASK_APPORTION ota on ot.id = ota.task_id LEFT JOIN T_OKR_PRODUCT_INFO opt ON opt.id = ota.apportion_name_id left join T_OKR_CUSTOMER oc on oc.id = ota.apportion_name_id left join T_OKR_APPORTION_CATEGORY oac on oac.id = ota.category_id left join T_OKR_TEAMS ot2 on ot2.id = ot.belong_team LEFT join T_OKR_TASK_KR_REL otkr on otkr.task_id = ot.id LEFT join T_OKR_MANAGE_OBJECTIVES omo on omo.id = otkr.kr_id WHERE 1=1
AND otur.user_id = #{condition.userId}
#{userId}
AND oac.id = #{condition.categoryId}
AND ot2.id = #{condition.teamId}
AND ot.id = #{condition.taskId}
AND omo.id = #{condition.okrId}
)t
where product_id = #{condition.productId}
